    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        try{
            Connection cn = DriverManager.getConnection("jdbc:mysql://localhost/plani", "root", "");
            PreparedStatement pst = cn.prepareStatement("select COD_USUARIO from usuario where COD_USUARIO = ?");
            pst.setString(1, PW1.getText().trim());
            
            ResultSet rs = pst.executeQuery();
            
            if(rs.next()){
                PW.setText("");
                US.setText("");
                JOptionPane.showMessageDialog(null, "PERMISO CONCEDIDO");
                MENU X= new MENU();
                X.setVisible(true);
            } else {
                JOptionPane.showMessageDialog(null, "NO SE ENCONTRO ");
            }
            
        }catch (Exception e){
               JOptionPane.showMessageDialog(null, "PERMISO DENEGADO");
        }
        
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

